import React, { useEffect } from 'react'
import { Link } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import { Row, Col } from 'react-bootstrap'
import Product from '../components/Product'
import Message from '../components/Message'
import Loader from '../components/Loader'
import Paginate from '../components/Paginate'
import ProductCarousel from '../components/ProductCarousel'
import Meta from '../components/Meta'
import { listProducts,createStore, listStore } from '../actions/productActions'
import SearchBox from '../components/SearchBox'
import { Route } from 'react-router-dom'
import Store from '../components/Store'



const HomeScreen = ({ match }) => {

  /**/
  const keyword = match.params.keyword

  const pageNumber = match.params.pageNumber || 1

  const dispatch = useDispatch()
  const storeList = useSelector((state) => state.productList)
  const { store } = storeList

  const request = () => {
    console.log(storeList)
   }

  const createProductHandler = () => {
    dispatch(createStore())
  }


  useEffect(() => {
    dispatch(listProducts(keyword, pageNumber),listStore())
  }, [dispatch, keyword, pageNumber])

  return (
    <>
      <Meta />
      <button
        onClick={request}
        className="btn btn-secondary product-button ">
        request
      </button>
      <h2 className='text-center'>Produtos cadastrados </h2>
      <Route render={({ history }) => <SearchBox history={history} />} />
      {!keyword ? (
        <ProductCarousel />
      ) : (
        <Link to='/' className='btn btn-light'>
          Go Back
        </Link>
      )}
      <h1>Lojas em destaque:({store?.length})</h1>

      <Store />



    </>
  )
}

export default HomeScreen
