import types from './types'

export function setCustomer(customer) {
	return { type: types.SET_CUSTOMER, customer };
}
export default function requestall() {
	return { type: types.REQUEST_STORES};
}

export function setall(stores) {
	return { type: types.SET_STORES, stores};
}

export function requeststores() {
	return { type: types.REQUEST_STORES};
}

export function requestproducts() {
	return { type: types.REQUEST_PRODUCTS};
}

export function setstores(stores) {
	return { type: types.SET_STORES, stores};
}

export function setproducts(produtos) {
	return { type: types.SET_PRODUCTS, produtos};
}

export function setShopMapSelected(store) {
	return { type: types.SET_STORE_MAP_SELECTED, store};
}

export function setMapCenter(location) {
	return { type: types.SET_MAP_CENTER, location};
}
///////////
export function requestuserid(id) {
	return { type: types.REQUEST_USERID, id};
}

export function setuserid(user) {
	return { type: types.SET_USERID, user};
}
//////////
export function requestStore(id) {
	return { type: types.REQUEST_STORE, id};
}

export function setStore(store) {
	return { type: types.SET_STORE, store};
}

export function toogleCartProduct(product) {
	return { type: types.TOGGLE_CART_PRODUCT, product};
}
