import React, { useState, useEffect } from 'react'
import axios from 'axios'
import { PayPalButton } from 'react-paypal-button-v2'
import { Link } from 'react-router-dom'
import { Row, Col, ListGroup, Image, Card, Button } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import Message from '../components/Message'
import Loader from '../components/Loader'
import {
  getOrderDetails,
  payOrder,
  deliverOrder,
} from '../actions/orderActions'
import {
  ORDER_PAY_RESET,
  ORDER_DELIVER_RESET,
} from '../constants/orderConstants'
import { listProductDetails, updateOrderProduct, updateProduct } from '../actions/productActions'
import { PRODUCT_UPDATE_RESET } from '../constants/productConstants'



const OrderScreen = ({ match, history }) => {
  const orderId = match.params.id

  const [sdkReady, setSdkReady] = useState(false)

  const dispatch = useDispatch()

  const orderDetails = useSelector((state) => state.orderDetails)
  const { order, loading, error } = orderDetails

  const orderPay = useSelector((state) => state.orderPay)
  const { loading: loadingPay, success: successPay } = orderPay

  const orderDeliver = useSelector((state) => state.orderDeliver)
  const { loading: loadingDeliver, success: successDeliver } = orderDeliver

  const userLogin = useSelector((state) => state.userLogin)
  const { userInfo } = userLogin

  if (!loading) {
    //   Calculate prices
    const addDecimals = (num) => {
      return (Math.round(num * 100) / 100).toFixed(2)
    }

    order.itemsPrice = addDecimals(
      order.orderItems.reduce((acc, item) => acc + item.price * item.qty, 0)
    )
  }

  useEffect(() => {
    if (!userInfo) {
      history.push('/login')
    }

    const addPayPalScript = async () => {
      const { data: clientId } = await axios.get('/api/config/paypal')
      const script = document.createElement('script')
      script.type = 'text/javascript'
      script.src = `https://www.paypal.com/sdk/js?client-id=${clientId}`
      script.async = true
      script.onload = () => {
        setSdkReady(true)
      }
      document.body.appendChild(script)
    }

    if (!order || successPay || successDeliver || order._id !== orderId) {
      dispatch({ type: ORDER_PAY_RESET })
      dispatch({ type: ORDER_DELIVER_RESET })
      dispatch(getOrderDetails(orderId))
    } else if (!order.isPaid) {
      if (!window.paypal) {
        addPayPalScript()
      } else {
        setSdkReady(true)
      }
    }
  }, [dispatch,
    history,
    orderId,
    successPay,
    successDeliver,
    order,
    userInfo])

  const successPaymentHandler = (paymentResult) => {
    console.log(paymentResult)
    dispatch(payOrder(orderId, paymentResult))
  }

  const deliverHandler = () => {
    dispatch(deliverOrder(order))
  }

  const productDetails = useSelector((state) => state.productDetails)
  const { product } = productDetails

  const [name, setName] = useState(product.name)
  const [store_id, setStoreId] = useState(product.store_id)
  const [price, setPrice] = useState(product.price)
  const [image, setImage] = useState(product.image)
  const [brand, setBrand] = useState(product.brand)
  const [category, setCategory] = useState(product.category)
  const [countInStock, setCountInStock] = useState(product.countInStock)
  const [description, setDescription] = useState(product.description)



  const submitHandler = (e) => {
    sub,
    sub2
  }

  const sub = () =>{
    order.orderItems.map((item) => (
      dispatch(listProductDetails(item?.product))

    ))

  }
  const sub2 = () =>{
    order.orderItems.map((item) => (
      dispatch(
        updateOrderProduct({
          _id: item?.product,
          countInStock: countInStock - item?.qty
        })
      ),
      dispatch({ type: PRODUCT_UPDATE_RESET })

    ))

  }
  /*
    order.orderItems.map((item) => (
      dispatch(
        updateProduct({
          _id: item?.product,
          name,
          store_id,
          price,
          image,
          brand,
          category,
          description,
          countInStock: countInStock - item?.qty
        })
      )
    ))
  */


return loading ? (
  <Loader />
) : error ? (
  <Message variant='danger'>{error}</Message>
) : (
  <>
    <h1>Pedido {order._id}</h1>
    <Row>
      <Col md={8}>
        <ListGroup variant='flush'>
          <ListGroup.Item>
            <h2>Frete</h2>
            <p>
              <strong>Nome: </strong> {order.user.name}
            </p>
            <p>
              <strong>Email: </strong>{' '}
              <a href={`mailto:${order.user.email}`}>{order.user.email}</a>
            </p>
            <p>
              <strong>Endereço:</strong>
              {order.shippingAddress.address}, {order.shippingAddress.city}{' '}
              {order.shippingAddress.postalCode},{' '}
              {order.shippingAddress.country}
            </p>
            {order.isDelivered ? (
              <Message variant='success'>
                Enviado {order.deliveredAt}
              </Message>
            ) : (
              <Message variant='danger'>Processando</Message>
            )}
          </ListGroup.Item>

          <ListGroup.Item>
            <h2>Forma de pagamento</h2>
            <p>
              <strong>Forma: </strong>
              {order.paymentMethod}
            </p>
            {order.isPaid ? (
              <Message variant='success'>Aprovado {order.paidAt}</Message>
            ) : (
              <Message variant='danger'>Não aprovado</Message>
            )}
          </ListGroup.Item>

          <ListGroup.Item>
            <h2>Produtos</h2>
            {order.orderItems.length === 0 ? (
              <Message>Order is empty</Message>
            ) : (
              <ListGroup variant='flush'>
                {order.orderItems.map((item, i) => (
                  <ListGroup.Item key={i}>
                    <Row>
                      <Col md={1}>
                        <Image
                          src={item.image}
                          alt={item.name}
                          fluid
                          rounded
                        />
                      </Col>

                      <Col>
                        {item.product}
                      </Col>

                      <Col>
                        <Link to={`/product/${item.product}`}>
                          {item.name}
                        </Link>
                      </Col>
                      <Col md={4}>
                        {item.qty} x R${item.price} = R${item.qty * item.price}
                      </Col>
                    </Row>
                  </ListGroup.Item>
                ))}
              </ListGroup>
            )}
          </ListGroup.Item>
        </ListGroup>
      </Col>
      <Col md={4}>
        <Card>
          <ListGroup variant='flush'>
            <ListGroup.Item>
              <h2>Detalhes do Pedido</h2>
            </ListGroup.Item>
            <ListGroup.Item>
              <Row>
                <Col>Items</Col>
                <Col>R${order.itemsPrice}</Col>
              </Row>
            </ListGroup.Item>
            <ListGroup.Item>
              <Row>
                <Col>Frete</Col>
                <Col>R${order.shippingPrice}</Col>
              </Row>
            </ListGroup.Item>
            <ListGroup.Item>
              <Row>
                <Col>Taxa</Col>
                <Col>R${order.taxPrice}</Col>
              </Row>
            </ListGroup.Item>
            <ListGroup.Item>
              <Row>
                <Col>Total</Col>
                <Col>R${order.totalPrice}</Col>
              </Row>
            </ListGroup.Item>
            {!order.isPaid && (
              <ListGroup.Item>
                {loadingPay && <Loader />}
                {!sdkReady ? (
                  <Loader />
                ) : (
                  <PayPalButton
                    currency_code="BRL"
                    amount={
                      order.totalPrice}
                    onSuccess={successPaymentHandler}
                  />
                )}
              </ListGroup.Item>
            )}
            {loadingDeliver && <Loader />}
            {userInfo &&
              userInfo.isAdmin &&
              order.isPaid &&
              !order.isDelivered && (
                <ListGroup.Item>
                  <Button
                    type='button'
                    className='btn btn-block'
                    onClick={deliverHandler}
                  >
                    Despachar
                  </Button>
                  <Button
                    type='button'
                    className='btn btn-block'
                    onClick={submitHandler}
                  >
                    atualizar
                  </Button>

                </ListGroup.Item>
              )}
          </ListGroup>
        </Card>
      </Col>
    </Row>
  </>
)
}

export default OrderScreen
