import React from 'react'
import GoogleMapReact from 'google-map-react'
import Marker from './Marker';

const Map = ({ stores }) => {
    const defaultProps = {
        center: {
            lat: -15.819391380128794,
            lng: -48.082514775944325,
        },
        zoom: 13
    };

    const [selectedStore, setSelectedStore] = React.useState(null);

    return (
        <div style={{ height: '700px', width: '100%' }}>
            <GoogleMapReact
                bootstrapURLKeys={{ key: "" }}
                center={selectedStore ? { lat: selectedStore.location.lat, lng: selectedStore.location.lng } : defaultProps.center}
                defaultZoom={defaultProps.zoom}
            >
                {stores?.map(p => (
                    <Marker store={p} setSelectedStore={setSelectedStore} lat={p.location.lat} lng={p.location.lng} image={p.image} />
                ))}
            </GoogleMapReact>
        </div>
    );
}

export default Map;
