import React from 'react'
import { Link } from 'react-router-dom'
import { Card, Button } from 'react-bootstrap'
import Rating from './Rating'
import { useDispatch} from 'react-redux';
import { toogleCartProduct } from '../store/modules/shop/actions';


const Product = ({ product }) => {
  const dispatch = useDispatch();
  const added = product.findIndex((p) => p._id === product._id) !== -1;
  return (
    <Card className={`${added ? 'productremove active' : 'product'} col-3`}>

      <Link to={`/product/${product._id}`}>
        <Card.Img src={product.image} variant='top' />
      </Link>

      <Card.Body>
      <Card.Text as='h4'
        ><label className="badge badge-primary">R$ {product.price.toFixed(2)} </label></Card.Text>
        <Link to={`/product/${product._id}`}>
          <Card.Title as='div'>
            <strong>{product.name}</strong>
          </Card.Title>
        </Link>

        <Card.Text as='div'>
          <Rating
            value={product.rating}
            text={`${product.numReviews} `}
          />
        </Card.Text>
        <Button onClick={() => dispatch(toogleCartProduct(product))}
                      //onClick={addToCartHandler}
                      className='btn-block mt-1'
                      type='button'
                      disabled={product.countInStock === 0}
                    >
                      comprar
                    </Button>
      </Card.Body>
    </Card>
  )
}

export default Product
