import React from 'react'
import { useDispatch } from 'react-redux';
//import { toogleCartProduct } from '../../../store/modules/shop/actions';
import { addToCart, removeFromCart } from '../actions/cartActions'
import {  Form} from 'react-bootstrap'

const Cart = ({ product, p }) => {
    const dispatch = useDispatch();

    return (
        <div className=" col-12">
            <div className="row">
                <div className="col-3">
                    <img src={product.image} alt='' className="img-fluid" />
                </div>
                <div className="col-5">
                    
                    <b>{product.name} </b>

                    <h5>
                        <span className="badge bg-primary">R${product.price}</span>
                    </h5>
                    <Form.Control
                      as='select'
                      value={p?.qty}
                      onChange={(e) =>
                        dispatch(
                          addToCart(p.product, Number(e.target.value))
                        )
                      }
                    >
                      {[...Array(p.countInStock).keys()].map((x) => (
                        <option key={x + 1} value={x + 1}>
                          {x + 1}
                        </option>
                      ))}
                    </Form.Control>
                </div>

            </div>
        </div>
    );
};

export default Cart;
