import React from 'react'
import { Link } from 'react-router-dom'
import MarkerIcon from '../assets/marker.png'
import MarkerIconSelected from '../assets/marker-selected.png'

const Marker = ({ store, setSelectedStore, selectedStore  }) => {
    return (
        <Link to={`/store/${store?._id}`}>
            <div onClick={() => setSelectedStore(store)}>
                <img src={selectedStore && selectedStore._id === store._id ? MarkerIconSelected : MarkerIcon} alt="ok" />
                <img src={store?.image} alt="mdi-cart-outline" className="img-marker" />
            </div>
        </Link>
    );
}

export default Marker;
