import { useDispatch } from 'react-redux';
//import { toogleCartProduct } from '../../../store/modules/shop/actions';


const Cart = ({ product }) => {
    const dispatch = useDispatch();

    return (
        <div className=" col-12">
            <div

                className="row">
                <div className="col-3">
                    <img src={product.capa} alt='' className="img-fluid" />
                </div>
                <div className="col-5">
                    <h6>
                        <span className="badge bg-primary">R${product.preco.toFixed(2)}</span>
                    </h6>
                    <small>
                        <b>{product.nome} </b>
                    </small>
                </div>
                <div className="col-4 text-end my-auto">
                    <div classNmae="btn-group text-center pt-3" role="group" aria-label="Basic example">
                        <button type="button" className="btn btn-primary rounded-circle product-list-r"

                        >-</button>
                        <b> 1 </b>
                        <button type="button" className="btn btn-primary rounded-circle product-list-r"
                        >+</button>
                    </div>
                </div>

            </div>
        </div>
    );
};

export default Cart;
