import React, { useEffect } from 'react'
import { Link } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
//import { Row, Col } from 'react-bootstrap'
//import Product from '../components/Product'
//import Message from '../components/Message'
//import Loader from '../components/Loader'
//import Paginate from '../components/Paginate'
import ProductCarousel from '../components/ProductCarousel'
import Meta from '../components/Meta'
import { listProducts } from '../actions/productActions'
import { listStore } from '../actions/storeActions'
import SearchBox from '../components/SearchBox'
import { Route } from 'react-router-dom'
import Store from '../components/Store'



const HomeScreen = ({ match }) => {

  /**/
  const keyword = match.params.keyword

  const pageNumber = match.params.pageNumber || 1

  const dispatch = useDispatch()
  const productList = useSelector((state) => state.productList)
  const { loading, error, products, page, pages } = productList
  const request = () => {
    console.log(storeList)
  }
  const storeList = useSelector((state) => state.storeList)
  const { stores } = storeList
  useEffect(() => {
    dispatch(listStore(keyword, pageNumber), listProducts(keyword, pageNumber))
  }, [dispatch, keyword, pageNumber])

  return (
    <>
      <Meta />
      <button
        onClick={request}
        className="btn btn-secondary product-button ">
        request
      </button>
      <h2 className='text-center'>Produtos cadastrados ({products?.length})</h2>
      <Route render={({ history }) => <SearchBox history={history} />} />
      {!keyword ? (
        <ProductCarousel />
      ) : (
        <Link to='/' className='btn btn-light'>
          Go Back
        </Link>
      )}
      <h1>Lojas em destaque:({stores?.length})</h1>
      <ul className="col-12 store-list">
        {stores?.map((p) => (
          <Store key={stores?._id}
          store={p} />
        ))}
      </ul>

      <ul className="col-12 store-list">
        {stores?.map((stores) => (
          <ul className="col-12 store-list"
           key={stores?._id} >
            <Store stores={stores} />
          </ul>
        ))}
      </ul>

    </>
  )
}

export default HomeScreen
