import React from 'react'
//import {useSelector} from 'react-redux';
import GoogleMapReact from 'google-map-react'
import Marker from './Marker';
import  { useState } from 'react'

const Map = ({ stores }) => {
    const [selectedStore, setSelectedStore] = useState(null);
    const defaultProps = {
        center: {
            lat: -15.819391380128794,
            lng: -48.082514775944325,
        },
        zoom: 13
    };

    const center = selectedStore ? { lat: selectedStore.location.lat, lng: selectedStore.location.lng } : defaultProps.center;


    return (
        <div style={{ height: '700px', width: '100%' }}>
            <GoogleMapReact
                bootstrapURLKeys={{ key: "" }}
                defaultCenter={defaultProps.center}
                center={center}
                defaultZoom={defaultProps.zoom}
            >
                {stores?.map(p => (
                    <Marker 
                        store={p} 
                        lat={p.location.lat} 
                        lng={p.location.lng} 
                        image={p.image} 
                        selected={p._id === selectedStore?._id}
                        onClick={() => setSelectedStore(p)}
                    />
                ))}
            </GoogleMapReact>
        </div>
    );
}

export default Map;
