     dispatch(listProducts(keyword, pageNumber))
   }, [dispatch, keyword, pageNumber])
 */
   const { stores, } = useSelector((state) => state.shop)

   useEffect(() => {
       dispatch(requestall());
   }, [dispatch]);
  return (
    <>
      <Meta />
      <h2 className='text-center'>Produtos cadastrados </h2>
      <Route render={({ history }) => <SearchBox history={history} />} />
      {!keyword ? (
        <ProductCarousel />
      ) : (
        <Link to='/' className='btn btn-light'>
          Go Back
        </Link>
      )}
      <h1>Lojas em destaque:({stores?.length})</h1>

      {stores?.map((p) => (
        <Store store={p} />
      ))}


      <h1>Produtos em destaque:</h1>
      <button
        onClick={request}
        className="btn btn-secondary product-button ">
        request
      </button>
    </>
  )
}

export default HomeScreen

