import React, { useState, useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { requestStore } from '../../store/modules/shop/actions'

import Produto from '../../components/products/card';
import './styles.css';


const StoreScreen = ({ match }) => {

    const dispatch = useDispatch();
    const { store } = useSelector((state) => state.shop);

    useEffect(() => {
        dispatch(requestStore(match.params.id));
    }, [dispatch, match]);

    console.log(store.produtos)
    return (
        <div className="h-100">
            <div className="container">
                <div className="row">
                    <div className="col-2">
                        <img src={store.logo} alt=''
                            className="img-fluid store-image" />
                        <b>{store.nome}</b>

                        <div className="store-infos">
                            <span className="mdi mdi-star"></span>
                            <text>
                                <b>
                                    {store.avaliacoes}
                                </b>
                            </text>
                            <text>{store.categoria}</text> <br />
                            <span className="mdi mdi-crosshairs-gps"></span>
                            <text>2,9km</text>
                        </div>
                        <span className="badge badge-primary">Frete Gratis</span><br/>
                        <span className="badge badge-primary"><b>Produtos: {store.products?.length}</b></span>
                        
                    </div>
                    <div className="col-10">
                        <h5>Produtos:</h5>
                        <br />
                        <div className="row">
                            {store.products?.map((p) => (
                                <Produto product={p} />
                            ))}
                        </div>
                    </div>

                </div>
            </div>
        </div>

    );

}

export default StoreScreen;
