import { useDispatch, useSelector } from 'react-redux';
import { toogleCartProduct } from '../../../store/modules/shop/actions';
import React from 'react';
import './styles.css';

const Produto = ({product}) => {

    const dispatch = useDispatch();
	const { cart } = useSelector(state => state.shop)
	const added = cart.findIndex((p) => p._id === product._id) !== -1;

    return (
        <div 
        onClick={() => dispatch(toogleCartProduct(product))}
        className={`${added ? 'productremove active' : 'product'} col-3`}>

            <img src={product.capa} alt=''
            className="img-fluid align-center " 
            />
            <h4>
                <label className="badge badge-primary">R$ {product.preco.toFixed(2)} </label>
            </h4>
            <small>
                <b>{product.nome}</b>
            </small>


        </div>
    );
}

export default Produto;
